export default class OrderForm extends LightningElement {
    // Mapping between input names and selectedPatient fields
    patientFieldMapping = {
        firstName: 'FirstName__c',
        lastName: 'LastName__c',
        mi: 'MI__c',
        dob: 'DOB__c',
        sex: 'Sex__c',
        mrn: 'MRN__c',
        patientPhone: 'Phone__c',
        patientEmail: 'Email__c',
        patientAddress: {
            street: 'Address__c',
            postalCode: 'ZipCode__c',
            city: 'City__c',
            province: 'State__c',
            country: 'Country__c'
        }
    };

    handleRowAction(event) {
        const selectedRows = event.detail.selectedRows;
        console.log('Selected Rows:', selectedRows);
        if (selectedRows.length > 0) {
            const selectedPatient = selectedRows[0];
            console.log('Selected Patient:', selectedPatient);

            // Query only the inputs within the patient-info div
            const patientInputs = this.template.querySelectorAll('.patient-info lightning-input, .patient-info lightning-combobox, .patient-info lightning-radio-group, .patient-info lightning-input-address');

            patientInputs.forEach(input => {
                const fieldName = this.patientFieldMapping[input.name];
                if (fieldName) {
                    if (typeof fieldName === 'object') {
                        // Handle address fields
                        Object.keys(fieldName).forEach(key => {
                            input[key] = selectedPatient[fieldName[key]];
                        });
                    } else {
                        input.value = selectedPatient[fieldName];
                    }
                }
                console.log(input.name, input.value);
            });
        }
    }
}
