            searchFacilityAccounts({ facilitySearchTerm: '%' + this.facilitySearchTerm + '%' })
                .then(result => {
                    if (result.length !== 0) {
                        this.facilities = result;
                    } else {
                        this.facilities = undefined;
                    }
                    this.facilityError = undefined;
                })
                .catch(error => {
                    this.facilityError = error.body.message;
                    this.facilities = undefined;
                });
