```apex
public with sharing class QueryController {
    @AuraEnabled
    public static List<Treating_Physician_Information__c> searchPhysicianAccounts(String physicianSearchTerm, Boolean isNumeric) {
        try {
            String query;
            if (isNumeric) {
                query = 'SELECT Name, Id, FirstName__c, LastName__c, Phone__c, Email__c, NPI__c FROM Treating_Physician_Information__c';
                List<Treating_Physician_Information__c> physicians = Database.query(query);
                List<Treating_Physician_Information__c> resultantPhysicians = new List<Treating_Physician_Information__c>();
                for (Treating_Physician_Information__c physician : physicians) {
                    if (physician.NPI__c != null && String.valueOf(physician.NPI__c).contains(physicianSearchTerm)) {
                        resultantPhysicians.add(physician);
                    }
                }
                return resultantPhysicians;
            } else {
                String searchTerm = '%' + physicianSearchTerm + '%';
                query = 'SELECT Name, Id, FirstName__c, LastName__c, Phone__c, Email__c, NPI__c FROM Treating_Physician_Information__c WHERE ' +
                        'FirstName__c LIKE :searchTerm OR LastName__c LIKE :searchTerm';
                return Database.query(query);
            }
        } catch (Exception e) {
            System.debug('Error in searchPhysicianAccounts: ' + e.getMessage());
            throw new AuraHandledException(e.getMessage());
        }
    }
}
