import { LightningElement, track } from 'lwc';
import searchFacilityAccounts from '@salesforce/apex/QueryController.searchFacilityAccounts';

export default class OrderForm extends LightningElement {
    @track facilities;
    @track facilityError;
    facilitySearchTerm = '';

